            <th>User_id</th>
            <th>Episode_id</th>
            <th>Comment</th>
            <th>Rate</th>
            <th>actions</th>
        </tr>
        </thead>
        <tbody>
        {% for comment in comments %}
            <tr>
                <td>{{ comment.id }}</td>
                <td>{{ comment.userId }}</td>
                <td>{{ comment.episode.title }}</td>
                <td>{{ comment.comment }}</td>
                <td>{{ comment.rate }}</td>
                <td>
                    <a href=\"{{ path('comment_show', {'id': comment.id}) }}\">show</a>
                    <a href=\"{{ path('comment_edit', {'id': comment.id}) }}\">edit</a>
                </td>
            </tr>
