    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Gestion des épisodes{% endblock %}

{% block body %}
    <div class=\"m-5 container\">
        <h1>Tous les épisodes</h1>
        <div class=\"m-5\"><a href=\"{{ path('episode_new') }}\"><button type=\"button\" class=\"btn btn-primary\">Ajouter un épisode</button></a></div>


        <table class=\"table\">
            <thead>
            <tr>
                <th>Id</th>
                <th>Season_id</th>
                <th>Title</th>
                <th>Number</th>
                <th>Synopsis</th>
                <th>actions</th>
            </tr>
            </thead>
            <tbody>
            {% for episode in episodes %}
                <tr>
                    <td>{{ episode.id }}</td>
                    <td>{{ episode.seasonId }}</td>
                    <td>{{ episode.title }}</td>
                    <td>{{ episode.number }}</td>
                    <td>{{ episode.synopsis }}</td>
                    <td>
                        <a href=\"{{ path('episode_show', {'slug': episode.slug}) }}\">show</a>
                        <a href=\"{{ path('episode_edit', {'slug': episode.slug}) }}\">edit</a>
                    </td>
                </tr>
            {% else %}
                <tr>
                    <td colspan=\"6\">no records found</td>
                </tr>
            {% endfor %}
            </tbody>
        </table>
    </div>

{% endblock %}
", "episode/index.html.twig", "/home/wilder/PhpstormProjects/wild-series/templates/episode/index.html.twig");
